import React, { useState } from "react";
import axios from "axios";

function App() {
  const [input, setInput] = useState("");
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const checkNews = async () => {
    setLoading(true);
    try {
      const response = await axios.post("http://localhost:5000/api/check", { text: input });
      setResult(response.data);
    } catch (error) {
      console.error("Error checking news:", error);
    }
    setLoading(false);
  };

  return (
    <div style={{ padding: "2rem", maxWidth: 600, margin: "auto" }}>
      <h2>ðŸ“° Real-Time News Verifier</h2>
      <textarea
        rows={5}
        value={input}
        onChange={(e) => setInput(e.target.value)}
        placeholder="Paste news here..."
        style={{ width: "100%", marginBottom: "1rem" }}
      />
      <button onClick={checkNews} disabled={loading}>
        {loading ? "Checking..." : "Check News"}
      </button>
      {result && (
        <div style={{ marginTop: "2rem", padding: "1rem", border: "1px solid #ccc" }}>
          <h3>Status: {result.status}</h3>
          <p><b>ML Prediction:</b> {result.mlStatus} (Confidence: {(result.mlConfidence * 100).toFixed(2)}%)</p>
          <p><b>Fact Check:</b> {result.factCheck} by {result.factSource}</p>
          <p><b>Rating:</b> {result.factRating}</p>
        </div>
      )}
    </div>
  );
}

export default App;
